import React, { Component } from 'react';
import { Button, Form, FormGroup, Label, Input, Alert } from 'reactstrap';

const reg = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;

type IProps = {
  navigation: any;
};
type IState = {
  userName: string;
  email: string;
  password: string;
  confirmPassword: string;
  firstName: string;
  lastName: string;
  emailError: boolean;
  passwordError: boolean;
  invalidEmailError: boolean;
  c_passwordError:boolean;
  firstError:boolean;
  lastError:boolean;
  userNameError:boolean;
  confirmPasswordError:boolean;
};
class SignUp extends Component<IProps, IState> {
  constructor(props: any) {
    super(props);
    this.state = {
      firstName: '',
      lastName: '',
      email: '',
      password: '',
      confirmPassword: '',
      userName: '',
      emailError: false,
      passwordError: false,
      invalidEmailError: false,
      c_passwordError: false,
      firstError: false,
      lastError: false,
      userNameError: false,
      confirmPasswordError: false,
    };
  }

  usernameHandler = (event: any) => {
    this.setState({
      email: event.target.value
    })
  }

  passwordHandler = (event: any) => {
    this.setState({
      password: event.target.value
    })
  }
  registerHandler = () => {
    const { firstName, lastName, userName, email, password, confirmPassword } = this.state;
    if (firstName === '') this.setState({ firstError: true });
    else if (lastName === '') this.setState({ lastError: true });
    else if (userName === '') this.setState({ userNameError: true });
    else if (email === '') this.setState({ emailError: true });
    else if (reg.test(email) === false) this.setState({ invalidEmailError: true });
    else if (password === '') this.setState({ passwordError: true });
    else if (confirmPassword === '') this.setState({ confirmPasswordError: true });
    else if (password !== confirmPassword) this.setState({ c_passwordError: true });
    else {
      const user = {
        email,
        password
      };
      console.log("The Login User Are: ", user);
      // this.props.login(user);
    }
  }
  render() {
    const { firstError, lastError, emailError, passwordError, confirmPasswordError, c_passwordError, invalidEmailError, userNameError } = this.state;
    return (
      <>
        <div style={{ marginTop: '5%', marginLeft: '40%' }}>
          <Form style={{ width: '30%' }}>
          <FormGroup>
              <Label for="exampleEmail" >First Name</Label>
              <Input type="email" name="email" id="exampleEmail" placeholder="First Name"
                onChange={this.usernameHandler}
              />
            </FormGroup>
            <FormGroup>
              <Label for="exampleEmail" >Last Name</Label>
              <Input type="email" name="email" id="exampleEmail" placeholder="Last Name"
                onChange={this.usernameHandler}
              />
            </FormGroup>
            <FormGroup>
              <Label for="exampleEmail" >UserName</Label>
              <Input type="email" name="email" id="exampleEmail" placeholder="UserName"
                onChange={this.usernameHandler}
              />
            </FormGroup>
            <FormGroup>
              <Label for="exampleEmail" >Email</Label>
              <Input type="email" name="email" id="exampleEmail" placeholder="Email"
                onChange={this.usernameHandler}
              />
            </FormGroup>
            {emailError &&
              <Alert color="danger">
                Email Not Empty
             </Alert>
            }
            {invalidEmailError &&
              <Alert color="danger">
                Email Invalid
             </Alert>
            }
            <FormGroup>
              <Label for="examplePassword" >Password</Label>
              <Input type="password" name="password" id="examplePassword" placeholder="Password"
                onChange={this.passwordHandler}
              />
            </FormGroup>
            <FormGroup>
              <Label for="examplePassword" >Confrim Password</Label>
              <Input type="password" name="password" id="examplePassword" placeholder="Confrim Password"
                onChange={this.passwordHandler}
              />
            </FormGroup>
            {passwordError &&
              <Alert color="danger">
                Password Not Empty
             </Alert>
            }
            <Button theme="info" onClick={this.registerHandler}>Login</Button>
          </Form>
        </div>


      </>
    );
  }
}
const style = {
  margin: 15,
};
export default SignUp;
